    private List<String> TEST_DATA;

    @Setup
    public void setup() {
        this.TEST_DATA = Arrays.stream(TEST_MESSAGE.split(" "))
                .flatMap(str -> Stream.of(str, null, "", String.format(" %s ", str)))
                .collect(Collectors.toList());
    }

    @Benchmark
    public void removeDuplicatesNullsEmptyStringsWithStream(Blackhole bh) {
        bh.consume(Util.removeDuplicatesNullsEmptyStrings(this.TEST_DATA));
    }

    @Benchmark
    public void removeDuplicatesNullsEmptyStringsWithForLoop(Blackhole bh) {
        bh.consume(Util.removeDuplicatesNullsEmptyStrings_naiveForBased(this.TEST_DATA));
    }

    public static void main(String[] args) throws RunnerException {
        Options opt = new OptionsBuilder()
                .include(BenchmarkApiUtil.class.getSimpleName())
                .forks(1)
                .build();

        new Runner(opt).run();
    }
